% characteristic equationfunction y = r2reig(b)global L rho J0 Jp Mps = sin(b*L);c = cos(b*L);sh = sinh(b*L);ch = cosh(b*L);y = (s.*ch-c.*sh) ...   +2*Mp*b./rho.*(s.*sh) ...   +J0*b.^3/rho.*(c.*ch+1) ...   +2*Jp*b.^3./rho.*(c.*ch) ...   +Mp*(J0+Jp)*b.^4./rho^2.*(c.*sh-s.*ch) ...   +J0*Jp*b.^6./rho^2.*(-s.*ch-c.*sh) ...   +J0*Jp*Mp*b.^7./rho^3.*(1-c.*ch);